<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class Menu extends Model{	protected $table = 'menus';	public function submenus() {		return $this->hasMany('App\Models\SubMenu');    }	public static $rules = array(        'menu_name' => 'required',        'menu_order' => 'required',    );    public static $messages = array(        'menu_name.required' => 'Please Add Menu Name',        'menu_order.required' => 'Give The Order Number',    );}